import styled from 'styled-components';

export const NavMenu = styled.nav`
    display: flex;
    justify-content: space-between;
    padding: 0.5rem;
    align-items: center;
    margin-left: 1rem;
    margin-right: 1rem;

    ul {
        display: flex;
        gap: 4rem;

        @media(max-width: 800px) {
            display: none;
        }
    }
`

export const Logo = styled.div`
    font-size: 2rem;

`
