import styled from "styled-components";

export const MenuButton = styled.button`
    background-color: blue;
    border-radius: 5px;
    border: none;
    margin: 1rem 0 1rem 0rem;
    padding: 0.5rem;
    cursor: pointer;
    transition: 0.3s;

   &:hover {
       opacity: 0.7;
   }
`;
