import styled from 'styled-components';
import MyImage from '/.public'

export const Content = styled.div`
    padding: 0.5rem;
    margin-left: 2rem;

    li {
        display: flex;
        gap: 1rem;
        margin-top: 1rem;
    }
`

export const MainContentImg = styled.div`
background-image: url(${MyImage});
`
