import styled from 'styled-components';


export const Content = styled.div`
    padding: 0.5rem;
    margin-left: 5rem;
    display: flex;
    gap: 3rem;
    align-items: center;


    ul {
        display: flex;
        gap: 1rem;
        margin-top: 1rem;
    }

    img {
        width: 100%;
        min-width: 30%;
        max-width: 300px;
        margin-top: 1rem;
        border: 1px solid #add8e6;
        box-shadow: 0px 15px 15px #1C1C1C;
    }

    @media (max-width: 870px) {
        display: block;
        margin-left: 2rem;
    }

`
