import styled from 'styled-components';


export const Content = styled.div`
    padding: 0.5rem;
    margin-left: 2rem;
    display: flex;
    gap: 3rem;
    align-items: center;

    ul {
        display: flex;
        gap: 1rem;
        margin-top: 1rem;
    }

    img {
        width: 100%;
        min-width: 30%;
        max-width: 300px;
        margin-top: 1rem;
        box-shadow: 10px 10px 25px 10px #0ff;
    }

    @media (max-width: 800px) {
        display: block;
    }

`

export const Mask = styled.div`

    overflow: hidden;
`
