import styled from "styled-components";

export const NavButton = styled.button`
    background-color: blue;
    border-radius: 5px;
    border: none;
    margin: 1rem 0 1rem 0rem;
    padding: 0.7rem;
    cursor: pointer;
    transition: 0.3s;
    color: white;
    font-weight: 600;

    &:hover {
       transform: scale
   }
`
