import styled from "styled-components";

export const TextBoxx = styled.div`
        width: 100%;
        max-width: 250px;
        background: blue;
        padding: 0.7rem;
        border-radius: 10px 10px 10px 0px;
        box-shadow: 0px 15px 30px #141414;
        opacity: 0.8;
        z-index: 1;
        position: absolute;
        transform: translate(200px, 0px);

        @media (max-width: 500px) {

        }

        p {
            margin-top: 0;
            font-size: 0.7rem;
        }
`
