import styled from "styled-components";

export const NavButton = styled.button`
position: relative;
background-color: black;
width: 3rem;
height: 2px;
display: inline-block;
margin-top: 3.5rem;

&::before,
&::after {
  content: "";
  background-color: black;
  width: 3rem;
  height: 2px;
  display: inline-block;
  position: absolute;
  left: 0;
}
&::before {
  top: -0.8rem;
}
&::after {
  top: 0.8rem;
}

`
