import styled from 'styled-components';


export const Content = styled.div`
    padding: 0.5rem;
    margin-left: 8rem;
    display: flex;
    gap: 3rem;
    align-items: center;


    ul {
        display: flex;
        gap: 1rem;
        margin-top: 1rem;
    }

    img {
        width: 100%;
        min-width: 30%;
        max-width: 300px;
        margin-top: 1rem;
        border: 1px solid #add8e6;
        box-shadow: 3px 5px 15px white;
    }

    @media (max-width: 800px) {
        display: block;
        margin-left: 2rem;
    }

`

export const Mask = styled.div`

    overflow: hidden;
`
