import styled from 'styled-components';


export const Content = styled.div`
    padding: 0.5rem;
    margin-left: 2rem;
    display: flex;
    gap: 2rem;

    ul {
        display: flex;
        gap: 1rem;
        margin-top: 1rem;
    }

    img {
        width: 100%;
        min-width: 40%;
        max-width: 200px;
        margin-top: 1rem;
    }

    @media (max-width: 800px) {
        display: block;
    }

`
